import { useEffect, useState } from "react"
import Lesson from "./lesson"

const Semester = ({ semester, socket, lessons }) => {
    const displayLesson = () => {
        socket.emit('displayLesson', semester)
    }
    //console.log(`lessons from mongodb: ${lessons}`)
    //<button onClick={displayLesson} key={`message-${index}`}>{lesson.semester}</button>

    return (
        <div>
            <h1>{semester}</h1>
            <div className="card mt15 p15">
                {lessons.map((lesson, index) => {
                    if(lesson.semester == semester) {
                        console.log(lesson.semester)
                    }
                })}
            </div>
        </div>
    )
    
}


export default Semester
