import { useEffect, useState } from "react"
import { Socket } from "socket.io-client"

const Lesson = ({ lessonName, socket }) => {
    const [lessons, setLessons] = useState()

    useEffect(() => {
        socket.on("lessons-first-semester", (lessonsFirstSemester) => {
            setLessons(lessonsFirstSemester)
            console.log(lessons)
        })

        socket.on("lessons-second-semester", (lessonsSecondSemester) => {
            setLessons(lessonsSecondSemester)
        })
    }, [])
    return (
        <div>
            <div className="card mt15 p15">
            {/* {lessons[0].map((lesson, index) => (
              <div className="card mt15 p15" key={index}>
                {lesson}
              </div>
            ))} */}
            </div>
        </div>
    )


}

export default Lesson
