import { useEffect, useState } from "react"
import Lesson from "./lesson"

const Semester = ({ name, socket }) => {
    const [lessons, setLessons] = useState("")

    useEffect(() => {
        socket.on("lessons-first-semester", (lessonsFirstSemester) => {
            setLessons(lessonsFirstSemester)
        })

        socket.on("lessons-second-semester", (lessonsSecondSemester) => {
            setLessons(lessonsSecondSemester)
        })
    })

    if(name === "Semestrul 1") {
        socket.emit("first-semester", name)
        return (
            <div>
                 <Lesson semester={name} lessons={lessons}  socket={socket}/>
            </div>
        )
        }
