import { Socket } from "socket.io-client"

const ClassButton = ({ socket }) => {
    const [className, setClassName] = useState("Clasa a 9-a")
    const enterClass = () => {
        socket.emit("enter-class", className)
    }

    return (
        <div>
            <div className="card mt15 p15">
                <button onClick={enterClass}>Clasa a 9-a CONTENT</button>
            </div>
        </div>
    )
}

export default ClassButton
