        console.log(`lesson in semester: ${lesson.lesson}`)        
    });
    //const [lessons, setLessons] = useState()

    useEffect(() => {
        socket.emit("first-semester", name)
        socket.on('output-messages', info => {
            console.log("the message is received in lessonsFirstSemester also ")
          
        })
    }, [])

    return (
        <div>
            <h1>{name}</h1>
            {/* {
                lessons.forEach(lesson => {
                    <div>{lesson.lesson}</div>
                })
            } */}
        </div>
    )
    
}


