import { useEffect, useState } from "react"

const Semester = ({ name, socket, lessons }) => {
    const [lessonClicked, setLessonClicked] = useState(false)


    return (
        <div>
           
            { lessonClicked === false
                    ? ( <div>
                            <h1>{name}</h1>
                            <div className="card mt15 p15">
                                {lessons.map((lesson, index) => (
                                    <button onClick={() => setLessonClicked(true)} key={`message-${index}`}>{lesson.lesson}</button>
                                ))}
                            </div>
                        </div>)
                    : <Lesson socket={socket} />
            }
           
            
        </div>
    )
    
}


export default Semester
