import { useEffect, useState } from "react"
import LessonsFirstSemester from "./lessonsFirstSem"

const Semester = ({ name, socket }) => {
    const [lessons, setLessons] = useState()

    useEffect(() => {
        socket.emit("first-semester", name)
        socket.on('output-messages', message => {
            setLessons(message)
            console.log(message)
            // if (message.length) {
            //   message.forEach(message => {
            //     console.log(`This is one part ${message.lesson}`)
            //   })
            // }
          })
    
    }, [])
    
    return (
        <div >
            <h2>{name}</h2>
             <LessonsFirstSemester socket={socket} lessons={lessons}/>
        </div>
    )

}

export default Semester
