import { useEffect, useState } from "react";
import Parser from 'html-react-parser';

const ContentPLace = ({ socket }) => {
    const [part, setPart] = useState(null);
    const iframe = '<iframe width="560" height="315" src="https://www.youtube.com/embed/muF58WFsXMw?start=1" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>'

    useEffect(() => {
        socket.on("displayPartS", part => {
          setPart(part);
        });
      }, []);

    return (
        <div className="content-place">
            {Parser(iframe)}
        </div>
    )
}

export default ContentPLace
