import { io } from "socket.io-client"
import { useEffect, useState } from "react"
import Header from "./components/header"
import FirstPage from "./components/first-page"
import Footer from "./components/footer"
import ClassContent from "./components/firstPage/classes/class-content"
import Lesson from "./components/firstPage/classes/semesters/lesson"

function App() {
  const [connectedSocket, setConnectedSocket] = useState()
  const [data, setData] = useState()
  const [lessons, setLessons] = useState()

  useEffect (() => {
    const socket = io()

    socket.on("connected", () => {
      setConnectedSocket(socket)
    })

    socket.on("data", (receivedData) => {
      setData(receivedData)
      console.log(receivedData)
    })

    socket.on('send-lessons', lessonsReceived => {
      console.log(`the lessons received in app are  ${lessonsReceived} `)
      console.log(`the lessons received in app have this type:  ${typeof(lessonsReceived)}`)
      setLessons(lessonsReceived)
      console.log(`this is lessons: ${lessons}`)
       if (lessonsReceived.length) {
          lessonsReceived.forEach(lesson => {
              console.log(` A lesson looks like this in app: ${lesson.lesson}`)
          });
      }
    })
    // socket.on("display-lesson1", (lesson) => {
    //   setData(lesson)
    // })
  }, [])

  if (!connectedSocket) {
    return <h1>Waiting for connection...</h1>
  }

  console.log(`This is the data ${data}`)
  console.log(`This are the lessons in app ${lessons}`)

   if (data) {
    return (
      <ClassContent data={data} socket={connectedSocket} lessons={lessons}/>
    )} else {
      return (
        <div>
          <Header />
          <FirstPage socket={connectedSocket} lessons={lessons}/> 
          <Footer />
        </div>
    )}
}

export default App;
