import { useEffect, useState } from "react"
import Lesson from "./lesson"

const Semester = ({ name, socket }) => {
    const [lessonsFirstSem, setLessonsFirstSem] = useState("")
    const [lessonsSecondSem, setLessonsSecondSem] = useState("")

    useEffect(() => {
        socket.on("lessons-first-semester", (lessonsFirstSemester) => {
            setLessonsFirstSem(lessonsFirstSemester)
        })

        socket.on("lessons-second-semester", (lessonsSecondSemester) => {
            setLessonsSecondSem(lessonsSecondSemester)
        })
    })

    if(name === "Semestrul 1") {
        socket.emit("first-semester", name)
        return (
            <div>
                 <Lesson semester={name} lessons={lessonsFirstSem}  socket={socket}/>
            </div>
        )
    } 


}

export default Semester
