    }

    return (
        <div className="vertical-menu">
            <div className="text">
            <div className="card mt15 p15">
                {lessons.map((lesson, index) => (
                    <div>{index}
                      {lesson.parts.map((part, index) => (
                          <button onClick={() => displayPart(part.name)}>{part.name}</button>
                      ))}
                       {lesson.quizzes.map((quiz, index) => (
